const CustomModal: React.FC<{ item: User | null; onClose: () => void }> = ({ item, onClose }) => {
  if (!item) return null;

  return (
<Modal visible={true} onRequestClose={onClose} transparent>
  <TouchableOpacity style={{ flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: 'rgba(0, 0, 0, 0.5)' }} activeOpacity={1} onPressOut={onClose}>
    <TouchableWithoutFeedback>
      <View style={{ backgroundColor: 'white', padding: 15, borderRadius: 10, width: '80%' }}>
        
        <View style={{ alignItems: 'center', marginBottom: 50  }}>
          <Image source={{ uri: item.linkImagem }} style={{ width: 80, height: 80, borderRadius: 40,  position: 'absolute', top: -40,}} />
        </View>
        <View style= {{marginBottom: 50}}>
        <Text style={{ textAlign: 'center' }}>
          {item.descricao}
        </Text>
        </View>

        <View style={{ flexDirection: 'row', justifyContent: 'center', position: 'absolute', bottom: 10, width: '90%' }}>
          <TouchableOpacity onPress={() => Linking.openURL(item.linkGithub)}>
          <View>
          <Icon name="github" size={30} color="#000" />
          </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => Linking.openURL(item.linkLinkedin)}>
          <View style={{ backgroundColor: '#2867B2', borderRadius: 5, padding: 5, marginLeft: 5 }}>
          <Icon name="linkedin" size={20} color="#fff" />
  </View>
                 </TouchableOpacity>
        </View>
      </View>
    </TouchableWithoutFeedback>
  </TouchableOpacity>
</Modal>
  );
};



function Itens({ item, onPress }: { item: User, onPress: () => void }) {
  return (
    <TouchableOpacity onPress={onPress} style={{ marginVertical: 10 }}>
      <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between', marginLeft:40, marginRight:40   }}>
        <View style={{ flexDirection: 'row', marginLeft: '3%', alignItems: 'center' }}>
          <Image source={{ uri: item.linkImagem }} style={{ width: 40, height: 40, borderRadius: 30, marginRight: 10 }} />
          <View style={{ marginLeft: '1%' }}>
            <Text style={{ fontWeight: 'bold', fontSize: 20 }}>{item.nome}</Text>
            <Text>{item.funcao}</Text>
          </View>
        </View>
        <View style={{ flexDirection: 'row' }}>
          <Image source={{ uri: item.iconeDois }} style={{ marginLeft: '3%', width: 35, height: 35, marginRight: 20 }} />
          <Image source={{ uri: item.icone }} style={{ width: 25, height: 25, marginRight: 20, marginTop: 5 }} />
        </View>
      </View>
    </TouchableOpacity>
  );
}

export default function Lista() {
  const [selectedItem, setSelectedItem] = useState<User | null>(null);

  return (
    <View >
       <View style={{ alignItems: 'center' }}>
        <Image source={{ uri: "https://picsum.photos/200/300"}} style={{ width: 450, height: 250, borderRadius: 20, marginVertical: 50 }} />
      </View>
      <View style={{ flex: 1 }}>
      <FlatList
        data={usuarios}
        renderItem={({ item }) => <Itens item={item} onPress={() => setSelectedItem(item)} />}
        keyExtractor={(item, index) => index.toString()}
      />
       </View>
      <CustomModal item={selectedItem} onClose={() => setSelectedItem(null)} />
    </View>
  );
}


